// filter voor game naam

export const filterProductByTitle = (nameToBeSearched, toBeSearchedArray) =>{
    return toBeSearchedArray.filter(backlogProduct => {
        if(backlogProduct.title.toUpperCase().indexOf(nameToBeSearched.toUpperCase()) !== -1){
            return backlogProduct
        }
        return;
    })
}

// filter voor platform

export const filterProductByMaterial = (materialToBeSearched, toBeSearchedArray) =>{
    return toBeSearchedArray.filter(backlogProduct =>{
        let found = false;
        backlogProduct.platforms.forEach(platform =>{
            if(platform.toUpperCase() === platformToBeSearched.toUpperCase()){
                found = true;
            }
        })
        if(found){
            return backlogProduct;
        }
    })
}


// Filter voor type


export const filterGamesByType = (typeToBeSearched, toBeSearchedArray) =>{
    return toBeSearchedArray.filter(backlogProduct =>{
        let found = false
        backlogProduct.type.forEach(type =>{
            if(type.toUpperCase() === typeToBeSearched.toUpperCase()){
                found = true
            }
        })
        if(found){
            return backlogProduct;
        }
    })
}
