const ProductCard = (props) => {

    const productCardClicked = () => {
        props.onCardClicked(props.id);
    }

    const productClothingFilter = (event) => {
        props.filterStart(event)

    }

    return (
        <>
            <div className="buttonwrapper">

                <button className="buttonfilter" onClick={() => productClothingFilter("schoenen")} > Clothing</button>
                <button className="buttonfilter" onClick={() => productClothingFilter("accesiores")} > Binnen</button>
                <button className="buttonfilter" onClick={() => productClothingFilter("All Clothing")} > Alles</button>
            </div>
            <li onClick={productCardClicked} className="productsList__item ">
                <img className="productsList__img" src={props.productImg} alt={props.name} />
                <div className="productsList__fade">
                    <p className="productsList__imageText">{props.name}</p>
                </div>
            </li></>
    );
}

export default ProductCard;
