import ProductCard from "../ProductCard/ProductCard";
import Placeholder from "../Placeholder/Placeholder";
import "./RightPane.css";

const RightPane = ({ headerText, buttonSymbol, buttonText, productCards, onButtonClicked,   onProductCardClicked }) => {
    const [filter, setFilter] = useState("");
    let onCardClicked = (idFromCard) => {
        onProductCardClicked(idFromCard);
    }

    let productCardsToBeRendered = productCards.map(product => {
        if (product.name === "Placeholder") {
            return <Placeholder id={product.id} key={product.id} onCardClicked={onCardClicked} buttonSymbol="+" buttonText="Voeg product toe" />
        }
        return <ProductCard onCardClicked={onCardClicked} key={product.id} id={product.id} name={product.name} productImg={product.img} price={product.price} filterStart={filterStart}
            filterProductCard={filterProductCard} />

    });
    const filterStart = (FilterStartOption) => {
        setFilter(FilterStartOption);
    };
    let filterProductCard = productCards;
    const filteredProductCard = productCards.filter((product) => {
        if (filter === "") {
            return true;
        }
        if (filter === "alles") {
            return true;
        }
        return filter === product.type;
    });

    return (
        <>

            <section className="productsWrapper">
                <header className="productswrapper__header">
                    <h1 className="header__h1">{headerText || "Placeholder"}</h1>
                </header>
                <ul className="productsList">
                    {productCardsToBeRendered}
                </ul>
            </section>
        </>
    );
}

export default RightPane;
