import { useSelector } from "react-redux";
import "./Display.css";

const Display = (props) =>{
    const filteredGames = useSelector(state => {return state})

    let firstToBeRendered = false;

    const titlesToBeRendered = filteredGames.map(backlogProduct => {
        console.log("display filter")
        if(!firstToBeRendered){
            firstToBeRendered = true;
            return (
                <section className="display" key={backlogProduct.categorie}>
                    <h2>{backlogProduct.categorie}</h2>
                    <figure className="display__play">Play this!</figure>
                </section>
                );
        }
        return (
            <section key={backlogProduct.categorie} className="display">
                <h2>{backlogProduct.title}</h2>
            </section>
            );
    })

    return(
        <>
            {titlesToBeRendered}
        </>
    )
}

export default Display;
