import "./FilterButtons.css";

const Filter = ({ activeFilter, setActiveFilter }) => {
    const handleFilter = (category) => {
      setActiveFilter(category);
    };
  
    return (
      <section className="FilterButtons">
        <button
          className={`FilterButton ${activeFilter === "Schoenen" ? "active" : ""}`}
          onClick={() => handleFilter("Schoenen")}
        >
          Schoenen
        </button>
        <button
          className={`FilterButton ${activeFilter === "Accesiores" ? "active" : ""}`}
          onClick={() => handleFilter("Accesiores")}
        >
          Accesiores
        </button>
      </section>
    );
  };
  
  export default Filter;
