import React, { useState, useEffect } from "react";
import RightPane from "../RightPane/RightPane";
import Popup from "../Popup/Popup";
import productsObject from "../../../Data/products";
import "./Dashboard.css";

const Dashboard = ({ activeFilter }) => {
  const [productCards, setProductCards] = useState([]);

  useEffect(() => {
    setProductCards(productsObject.products);
  }, []);

  const filteredProductCards = activeFilter
    ? productCards.filter((product) =>
        product.categorie.includes(activeFilter)
      )
    : productCards;

  return (
    <section>
      <Popup />
      <RightPane productCards={filteredProductCards} />
    </section>
  );
};

export default Dashboard;
