import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { FilterProductsByTitle, FilterProductsByMaterial } from "../../../../Helpers/filterProducten";
import products from "../../../Data/products";

const BacklogForm = () => {
  // state voor elke input
  const [inputs, setInputs] = useState([
    {
      id: "title",
      value: "",
      label: "Title",
      filter: FilterProductsByTitle,
    },
    {
      id: "materials",
      value: "",
      label: "Materials",
      filter: FilterProductsByMaterial,
    },
  ]);

  const dispatch = useDispatch();

  // set state voor input value
  const onInputChanged = (e) => {
    const { id, value } = e.target;
    setInputs((prevState) =>
      prevState.map((input) => (input.id === id ? { ...input, value } : input))
    );
  };

  // on submit filter door elke input tegelijkertijd filtert op elke filter
  const submit = (event) => {
    event.preventDefault();
    let result = products;
    inputs.forEach((input) => {
      result = input.filter(input.value, result);
    });
    dispatch({ type: "FILTEREDPRODUCTS", payload: result });
  };

  const inputsToBeRendered = inputs.map((objectFromStateArray) => (
    <div key={objectFromStateArray.id} className="form__wrapper">
      <label className="form__label" htmlFor={objectFromStateArray.id}>
        {objectFromStateArray.label}
      </label>
      <input
        className="form__input"
        onChange={onInputChanged}
        type="text"
        id={objectFromStateArray.id}
        value={objectFromStateArray.value}
      />
    </div>
  ));

  return (
    <form className="form" onSubmit={submit}>
      <div className="formInputsWrapper">{inputsToBeRendered}</div>
      <button className="form__search" type="submit" onClick={submit}>
        SEARCH
      </button>
    </form>
  );
};

export default BacklogForm;
