import React, { useState } from "react";
import ProductCard from "../ProductCard/ProductCard";
import Placeholder from "../Placeholder/Placeholder";
import "./RightPane.css";

const RightPane = ({ headerText, buttonText, productCards }) => {
  const [filter, setFilter] = useState(null);

  const onButtonClicked = (filter) => {
    setFilter(filter);
  };

  const onProductCardClicked = (idFromCard) => {
    console.log("Product card clicked:", idFromCard);
  };

  let filteredProductCards = productCards;
  if (filter) {
    filteredProductCards = productCards.filter(
      (product) => product.name === filter
    );
  }

  let productCardsToBeRendered = filteredProductCards.map((product) => {
    if (product.name === "Placeholder") {
      return (
        <Placeholder
          id={product.id}
          key={product.id}
          onCardClicked={onProductCardClicked}
          buttonSymbol="+"
          buttonText="Voeg product toe"
        />
      );
    }
    return (
      <ProductCard
        onCardClicked={onProductCardClicked}
        key={product.id}
        id={product.id}
        name={product.name}
        productImg={product.img}
        price={product.price}
      />
    );
  });

  return (
    <>
      <header>
        <Filter onButtonClicked={onButtonClicked} />
      </header>
      <section className="productsWrapper">
        <header className="productswrapperheader">
          <h1 className="headerh1">{headerText || "Placeholder"}</h1>
        </header>
        <ul className="productsList">{productCardsToBeRendered}</ul>
      </section>
    </>
  );
};

export default RightPane;
