import React, { useState } from "react";
import CollectieNav from "./CollectieNav/CollectieNav";
import Filter from "./Filter/FilterButtons";
import Dashboard from "./CollectieDashboard/Dashboard/Dashboard";
import Footer from "../Footer/Footer";
import "./CollectiepaginaMain.css";

const CollectiePaginaMain = () => {
  const [activeFilter, setActiveFilter] = useState(null);

  const handleFilterChange = (filter) => {
    setActiveFilter(filter);
  };

  const filterProductCards = () => {
    if (activeFilter === null) {
      return productsObject.products; // Display all products when no filter is active
    }

    return productsObject.products.filter((product) =>
      product.categorie.includes(activeFilter)
    );
  };

  return (
    <section className="CollectiePaginaMain">
      <header>
        <CollectieNav />
        <Filter onFilterChange={handleFilterChange} />
      </header>
      <main>
        <Dashboard productCards={filterProductCards()} />
      </main>
      <footer>
        <Footer />
      </footer>
    </section>
  );
};

export default CollectiePaginaMain;
