import { useState, useEffect } from 'react';
import ProductCard from "../ProductCard/ProductCard";
import Placeholder from "../Placeholder/Placeholder";
import Filter from "../Filter/FilterButtons";


import itemsFromDataFile from "../../../Data/products";
import "./RightPane.css";

const RightPane = ({ headerText, buttonSymbol, buttonText, productCards, onButtonClicked, onProductCardClicked }) => {

    let onCardClicked = (idFromCard) => {
        onProductCardClicked(idFromCard);
    }

    const onFilter = (typeToBeFiltered) => {
        console.log("ik laat data  in en filter");
        const copy = [...defaultItems]
        const cardsToBeRendered = copy.filter(card => {
          if(card.props.type === typeToBeFiltered){
            return card
          }
        })



        setItems(cardsToBeRendered)
      }

      const [items, setItems] = useState([])
      const [defaultItems, setDefaultItems] = useState([])

     const onGalleryCardClicked = (collection) => {
        props.onGalleryCardClicked(collection)
      }

      useEffect(() => {
        let i = 0;
        let types = ["wijn", "wiskey", "bier"]

        const galleryCardsToBeRendered = itemsFromDataFile.map(collection => {
          let temp = 
          <Link key={types[i]} type={types[i]} onClick={() => onGalleryCardClicked(collection)} to={`/product/${types[i]}`}>
          <GalleryCard  items={collection} />
          </Link>
          i = i + 1;
          return temp
        })
        setItems(galleryCardsToBeRendered)
        setDefaultItems(galleryCardsToBeRendered)
      }, [])

    let productCardsToBeRendered = productCards.map(product => {
        if (product.name === "Placeholder") {
            return <Placeholder id={product.id} key={product.id} onCardClicked={onCardClicked} buttonSymbol="+" buttonText="Voeg product toe" />
        }
        return <ProductCard onCardClicked={onCardClicked} key={product.id} id={product.id} name={product.name} productImg={product.img} price={product.price} />

    });
    return (
        <>
        <header>
            <Filter onFilter={onFilter}/>
        </header>
            <section className="productsWrapper">
                <header className="productswrapper__header">
                    <h1 className="header__h1">{headerText || "Placeholder"}</h1>
                </header>
                <ul className="productsList">
                    {productCardsToBeRendered}
                </ul>
            </section></>
    );
}

export default RightPane;
