import React from "react";
import "./FilterButtons.css";

const Filter = ({ handleFilter }) => {
  const applyFilter = (filter) => {
    handleFilter(filter);
  };

  return (
    <section className="FilterButtons">
      <button className="FilterButton" onClick={() => applyFilter("button1")}>
        Button 1
      </button>
      <button className="FilterButton" onClick={() => applyFilter("button2")}>
        Button 2
      </button>
    </section>
  );
};

export default Filter;
