export const FilterProductsByTitle = (nameToBeSearched, toBeSearchedArray) => {
    return toBeSearchedArray.filter((product) => {
      if (product.title.toUpperCase().indexOf(nameToBeSearched.toUpperCase()) !== -1) {
        return product;
      }
    });
  };
  
  export const FilterProductsByMaterial = (typeToBeSearched, toBeSearchedArray) => {
    return toBeSearchedArray.filter((product) => {
      const found = product.materials.includes(typeToBeSearched.toUpperCase());
      if (found) {
        return product;
      }
    });
  };
