import React, { useState } from "react";
import ProductCard from "./ProductCard";

const Filter = ({ productCards }) => {
  const [filteredCards, setFilteredCards] = useState(productCards);

  const filterByCategory = (category) => {
    if (category === "All") {
      setFilteredCards(productCards);
    } else {
      const filtered = productCards.filter(
        (product) => product.categorie.includes(category)
      );
      setFilteredCards(filtered);
    }
  };

  return (
    <div>
      <button onClick={() => filterByCategory("All")}>All</button>
      <button onClick={() => filterByCategory("Schoenen")}>Schoenen</button>
      <button onClick={() => filterByCategory("Accesiores")}>Accessories</button>
      <button onClick={() => filterByCategory("Kleding")}>Kleding</button>
      <ul>
        {filteredCards.map((product) => (
          <ProductCard key={product.id} name={product.name} img={product.img} />
        ))}
      </ul>
    </div>
  );
};

export default Filter;
