import React from "react";
import "./FilterButtons.css";

const Filter = ({ onFilter }) => {
  const handleFilter = (category) => {
    onFilter(category);
  };

  return (
    <section className="FilterButtons">
      <button className="FilterButton" onClick={() => handleFilter(null)}>
        All
      </button>
      <button className="FilterButton" onClick={() => handleFilter("Schoenen")}>
        Schoenen
      </button>
      <button className="FilterButton" onClick={() => handleFilter("Accesiores")}>
        Accesiores
      </button>
      <button className="FilterButton" onClick={() => handleFilter("Kleding")}>
        Kleding
      </button>
    </section>
  );
};

export default Filter;
