// filter voor game naam

export const filterTitle = (nameToBeSearched, toBeSearchedArray) =>{
    return toBeSearchedArray.filter(products => {
        if(products.title.toUpperCase().indexOf(nameToBeSearched.toUpperCase()) !== -1){
            return products
        }
        return;
    })
}



// Filter voor matarial


export const filterProductsByMaterial = (typeToBeSearched, toBeSearchedArray) =>{
    return toBeSearchedArray.filter(products =>{
        let found = false
        products.type.forEach(type =>{
            if(type.toUpperCase() === typeToBeSearched.toUpperCase()){
                found = true
            }
        })
        if(found){
            return products;
        }
    })
}
