import React, { useState } from "react";
import CollectieNav from "./CollectieNav/CollectieNav";
import Filter from "./Filter/FilterButtons";
import Dashboard from "./CollectieDashboard/Dashboard/Dashboard";
import Footer from "../Footer/Footer";
import productsObject from "../";
import "./CollectiepaginaMain.css";

const CollectiePaginaMain = () => {
  const [productCards, setProductCards] = useState(productsObject.products);
  const [activeFilter, setActiveFilter] = useState(null);

  const applyFilter = (filter) => {
    setActiveFilter(filter);
    if (filter === "All") {
      setProductCards(productsObject.products);
    } else {
      const filteredProducts = productsObject.products.filter(
        (product) => product.categorie.includes(filter)
      );
      setProductCards(filteredProducts);
    }
  };

  return (
    <section className="CollectiePaginaMain">
      <header>
        <CollectieNav />
        <Filter onFilter={applyFilter} />
      </header>
      <main>
        <Dashboard productCards={productCards} />
      </main>
      <footer>
        <Footer />
      </footer>
    </section>
  );
};

export default CollectiePaginaMain;
