import { useState } from 'react';
import ProductCard from '../ProductCard/ProductCard';
import Placeholder from '../Placeholder/Placeholder';
import './RightPane.css';

import image1 from '../../img/product1.jpg';
import image2 from '../../img/product2.jpg';
import image3 from '../../img/product3.jpg';
import image4 from '../../img/product4.jpg';
import image5 from '../../img/product5.jpg';
import image6 from '../../img/product6.jpg';
import image7 from '../../img/product7.jpg';
import image8 from '../../img/product8.jpg';

const products = [
  {
    id: 1,
    name: 'Placeholder',
  },
  {
    id: 2,
    name: 'Jordan',
    img: image1,
    price: '$' + '130',
    category: ['Schoenen'],
    materials: ['COTTON', 'WOOL', 'POLYESTER', 'NYLON', 'RUBBER', 'LEATHER'],
    description: 'De nieuwste Jordan. Gemaakt uit 100% Recyclebare producten!',
  },
  {
    id: 3,
    name: 'Yellow Beanie',
    img: image2,
    price: '$' + '130',
    category: ['Accessoires'],
    materials: ['COTTON', 'WOOL', 'POLYESTER', 'NYLON', 'RUBBER', 'LEATHER'],
    description: 'De nieuwste Jordan. Gemaakt uit 100% Recyclebare producten!',
  },
  {
    id: 4,
    name: 'Red White Colored Cap',
    img: image3,
    price: '$' + '130',
    category: ['Kleding'],
    materials: ['COTTON', 'WOOL', 'POLYESTER', 'NYLON', 'RUBBER', 'LEATHER'],
    description: 'Een oldschool . Gemaakt uit 100% Recyclebare producten!',
  },
  {
    id: 5,
    name: 'Yellow Hood for the hood',
    img: image4,
    price: '$' + '130',
    category: ['Kleding'],
    materials: ['COTTON', 'WOOL', 'POLYESTER', 'NYLON', 'RUBBER', 'LEATHER'],
    description: 'De nieuwste Hoodie. Gemaakt uit 100% Recyclebare producten!',
  },
];

const RightPane = ({
  headerText,
  buttonSymbol,
  buttonText,
  productCards,
  onButtonClicked,
  onProductCardClicked,
}) => {
  const [filteredProductCards, setFilteredProductCards] = useState(productCards);
  const [showAll, setShowAll] = useState(true);

  const onCardClicked = (idFromCard) => {
    onProductCardClicked(idFromCard);
  };

  const filterByCategory = (category) => {
    if (category === 'all') {
      setFilteredProductCards(products.filter((product) => product.name !== 'Placeholder'));
    } else {
      setFilteredProductCards(products.filter((product) => product.category.includes(category)));
    }
    setShowAll(false);
  };

  const toggleFilter = () => {
    if (showAll) {
      setFilteredProductCards(products.filter((product) => product.name !== 'Placeholder'));
    } else {
      setFilteredProductCards(productCards);
    }
    setShowAll(!showAll);
  };

  let productCardsToBeRendered = filteredProductCards.map((product) => {
    if (product.name === 'Placeholder') {
      return (
        <Placeholder
          id={product.id}
          key={product.id}
          onCardClicked={onCardClicked}
          buttonSymbol="+"
          buttonText="Voeg product toe"
        />
      );
    }
    return (
      <ProductCard
        onCardClicked={onCardClicked}
        key={product.id}
        id={product.id}
        name={product.name}
        productImg={product.img}
        price={product.price}
      />
    );
  });

  return (
    <section className="productsWrapper">
      <header className="productswrapper__header">
        <h1 className="header__h1">{headerText || 'Placeholder'}</h1>
      </header>
      <div className="categoryButtons">
        <button className="categoryButton" onClick={() => filterByCategory('all')}>
          All
        </button>
        <button className="categoryButton" onClick={() => filterByCategory('Schoenen')}>
          Schoenen
        </button>
        <button className="categoryButton" onClick={() => filterByCategory('Kleding')}>
          Kleding
        </button>
        <button className="categoryButton" onClick={() => filterByCategory('Accessoires')}>
          Accessoires
        </button>
      </div>
      <button className="filterButton" onClick={toggleFilter}>
        {showAll ? 'Show All' : 'Filter'}
      </button>
      <ul className="productsList">{productCardsToBeRendered}</ul>
    </section>
  );
};

export default RightPane;
